mapper.select:
SELECT * FROM xl_file WHERE folder_id = (SELECT id FROM xl_file WHERE path = #{path}) AND status = 1
AND type_id IN (SELECT id FROM xl_file_type WHERE pid = (SELECT id FROM xl_file_type WHERE name = #{type}))
ORDER BY id DESC LIMIT #{start}, #{size}

SELECT * FROM xl_file WHERE folder_id = #{folderId} AND status = 1
AND type_id IN (SELECT id FROM xl_file_type WHERE pid = (SELECT id FROM xl_file_type WHERE name = #{type}))
ORDER BY id DESC LIMIT #{start}, #{size}
